            email:'fahadchowdury98@gmail.com',
            password:'4589',
            isAdmin:true,
    
        });
        const newUser = await user.save();
        res.send(newuser);
        
    } catch (error) {
        res.send({msg: error.message})
        
    }
    
} );

export default router;

